function my_triangle(k)
	triangle = Vector{Float64}(undef, 1000)
	x_tri = range(0,4,1000)
	for j = 1:1000
	  for i =1:k
	    triangle[j] = triangle[j]+((-1)^i)*sin.(2*Ï€*(j-1)*(4/999)*(2*i-1))/(2*i-1)^2
	  end
		triangle[j] = triangle[j]*((-8)/(Ï€^2))
	end
return  triangle
end

