function my_rect(k,n)
	taxis = range(0, 2π, length= n)

	summe = zeros(n,1)

	for xi in 1:2:k
		summe = summe.+sin.(xi.*taxis)./xi
	end
	result= summe.*4/π
	return result
end
end;

