begin
LSB3Bit=2^(32-3);
signalRound3Bit=map(Int32,round.(2^30 .* sin.(2*pi*t*10).+0.499999*LSB3Bit))
	plot(t,shiftInteger(signalRound3Bit,3)./2^30,title=md""" 3 Bit ADC""", label="Gerundet", width=3,size=(750,400),xlabel="Zeit/s",ylabel="Spannung/V",left_margin = 10Plots.mm,bottom_margin = 10Plots.mm)
