function select_QRS(data,t,idx_r_spike)
R=determine_r_spike(data)
ìndizes_R=R[idx_r_spike]
ìndizes=[ìndizes_R-25,ìndizes_R+25]
time_ìndizes=ìndizes./t
return ìndizes,time_ìndizes
end

