function determine_r_spike(data)
	threshold = 0.0016
	rspike_indices_thresh = findall(data .> threshold)
	gap_threshold = 100
	gap_indices = [i for i in 2:length(rspike_indices_thresh) if rspike_indices_thresh[i] - rspike_indices_thresh[i-1] > gap_threshold]
	rangeIndices = [1; gap_indices; length(rspike_indices_thresh)]
	ranges = [rangeIndices[i-1]:rangeIndices[i]-1 for i in 2:length(rangeIndices)]
	rspike_indices_maxima = [rspike_indices_thresh[range_][findmax(data[rspike_indices_thresh[range_]])[2]] for range_ in ranges]
	return rspike_indices_maxima
end;

