# ╔═╡ 9b05928a-0c71-4b6f-bb01-8d7e955ab3b8
function fft_plot(N, fs, freq)
	n = range(0,N-1)
	y1 = sin.(2π*freq*n/fs)
	p1 = plot(n, y1,label=:none)
	plot!(twiny(p1),n/fs,y1,alpha=0,label=:none, xlabel="\nn\nt / s", mirror=false,tick_direction=:out,tickfontvalign=:top)
	p2 = plot(n, real.(fft(y1)), lw=3,line=:stem, marker=:circle, label="Realteil", legend_position=:bottom, ylims=(-0.55N,0.55N))
	p3 = plot(n, imag.(fft(y1)), lw=3,line=:stem, marker=:circle, label="Imaginärteil", legend_position=:bottom, ylims=(-0.55N,0.55N))
	plot!(twiny(p2),(0:N-1)*fs/N,imag.(fft(y1)),alpha=0,label=:none, xlabel="\nk\nf / Hz", mirror=false, tick_direction=:out, tickfontvalign=:top)
	plot!(twiny(p3),(0:N-1)*fs/N,imag.(fft(y1)),alpha=0,label=:none, xlabel="\nk\nf / Hz", mirror=false, tick_direction=:out, tickfontvalign=:top)
	#p4 = plot(real.(fft(y1)), imag.(fft(y1)))
	#p3 = plot(n, angle.(fft(y1)[3]), lw=3,line=:stem, marker=:circle, label="Imaginärteil", legend_position=:bottom)
	plot(p1,p2,p3,layout=(@layout [a [ b; c]]),size=(1000,500), plot_title="fs = $fs Hz, N = $N ",bottom_margin = 12Plots.mm, labelfontsize=9)
end;

