function negativeS(S)
	S2=copy(S)	
	S2[S.>0].=0
	return S2
end;

# ‚ïî‚ïê‚ï° bbb9060a-5004-423c-a01e-bbec85fb49b8
function positiveS(S)
S2=copy(S)
	S2[S.<0].=0
	return S2
end;

