# ‚ïî‚ïê‚ï° 5875d3be-9e01-417d-80d1-5caf5513f33f
begin
function FourLayerPlot(S2, fr)
	P13=plot(time,S2,width=6,size=(1000,1000), title="Frequenz $fr Hz", ylabel="Spannung/ V", xlabel="Zeit/s",left_margin = 10Plots.mm, bottom_margin = 10Plots.mm, ylims=(-3,3),label="Signal")
	plot!(time,sin.(fr.*r),width=2,color="red",label="Testsignal")
	Sy2=S2.*sin.(fr.*r)
	S2n=copy(Sy2)
	S2p=copy(Sy2)
	S2p[S2p.<0].=0
	S2n[S2n.>0].=0
	result2=round(sum(Sy2)./length(Sy2)*2,digits=2)
	P14=plot(time,zeros(size(S2p)),fillrange=S2p,fillalpha=1,c=1,title=md"Fl√§che unter der Kurve $F(t)$", xlabel="Zeit/s",ylabel="Spannung/V")
	plot!(time,zeros(size(S2n)),fillrange=S2n,fillalpha=1,c=3)
	plot!(time,Sy2,width=1,color="red",ylims=(-3,3))
	P15=plot(time,Sy2,width=1,color="red",ylims=(-3,3),title=md"Multiplikation von Signal und Sinus $F(t)$", xlabel="Zeit/s")
	P16=plot([0,0], [0,result2], marker =:circle, arrow=true, arrowsize=0.1, ylims=(-1.5,1.5),xlims=(-1.5,1.5)) 
	plot!(sin.(0:0.01:2*pi),cos.(0:0.01:2*pi),title=md"""Vektordarstellung des integrierten Signals """)
	return plot(P13,P15,P14,P16)
end;
function ComplexFourLayerPlot(S2,fr;width=5,maxsample=1000)
	P23=plot(S2,width=width,size=(1000,1000), title="Frequenz $fr Hz", ylabel="Spannung/ V", xlabel="Samples",left_margin = 10Plots.mm, bottom_margin = 10Plots.mm, ylims=(-3,3),label="Signal",xlims=(0,maxsample))
	#plot!(sin.(fr.*r),width=2,color="red",label="Sinus Testsignal")
	Sy2=S2.*sin.(fr.*r)
	Cy2=S2.*cos.(fr.*r)
	S2n=negativeS(Sy2)
	S2p=positiveS(Sy2)
	C2n=negativeS(Cy2)
	C2p=positiveS(Cy2)
	resultSin=sum(Sy2)./length(Sy2)*2
	resultCos=sum(Cy2)./length(Cy2)*2
	P24=plot(4*ones(size(S2p)),fillrange=S2p.+4,fillalpha=1,c=1,title=md"Fl√§che unter der Kurve $F(t)$", label="Positiv Sinus",legend=:right,xlims=(0,maxsample))
	plot!(4*ones(size(S2n)),fillrange=S2n.+4,fillalpha=1,c=3,label="Negativ Sinus")
	plot!(-4*ones(size(C2n)),fillrange=C2n.-4,fillalpha=1,c=4,label="Positiv Cosinus")
	plot!(-4*ones(size(C2p)),fillrange=C2p.-4,fillalpha=1,c=6,label="Negativ Cosinus")
	plot!(Sy2.+2,width=1,color="red",ylims=(-5,5),label="sin(2pift) s(t)")
	plot!(2 .*ones(size(Sy2)),color="black",label="")
	plot!(-2 .*ones(size(Sy2)),color="black",label="")

	plot!(Cy2.-2,width=1,color="green",label="cos(2pift) s(t)",yticks=[],ylims=(-6,6))
	P25=plot(Sy2,width=1,color="red",ylims=(-3,3),title=md" $F(t)$ ",label="Sinus Multiplikation",xlims=(0,maxsample))
	plot!(Cy2,width=1,color="green",label="Cosinus Multiplikation")
	P26=plot([0,resultCos],[0,resultSin], marker =:circle, arrow=true, arrowsize=0.1, ylims=(-1.5,1.5),xlims=(-1.5,1.5)) 
	plot!(sin.(0:0.01:2*pi),cos.(0:0.01:2*pi),title=md"""Vektor auf der Imagin√§ren Ebene $ \sum F(t)$ """)
	plot(P23,P25,P24,P26)
end
	function FourLayerPlotCos(S2, fr)
	P13=plot(time,S2,width=6,size=(1000,1000), title="Frequenz $fr Hz", ylabel="Spannung/ V", xlabel="Zeit/s",left_margin = 10Plots.mm, bottom_margin = 10Plots.mm, ylims=(-3,3),label="Signal")
	plot!(time,cos.(fr.*r),width=2,color="red",label="Testsignal")
	Sy2=S2.*cos.(fr.*r)
