	plot!(time,zeros(size(S2n)),fillrange=S2n,fillalpha=1,c=3)
	plot!(time,Sy2,width=1,color="red",ylims=(-3,3))
	P15=plot(time,Sy2,width=1,color="red",ylims=(-3,3),title=md"Multiplikation von Signal und Cosinus $F(t)$", xlabel="Zeit/s")
	#P16=plot([1,1000], [result2,result2],ylims=(-1.3,1.3),title=md"""Summensignal $B=\quad\sum F(t)$ $result2""" )
	P16=plot([0,result2],[0,0], marker =:circle, arrow=true, arrowsize=0.1, ylims=(-1.5,1.5),xlims=(-1.5,1.5)) 
	plot!(sin.(0:0.01:2*pi),cos.(0:0.01:2*pi),title=md"""Vektordarstellung des integrierten Signals""")
