# ╔═╡ 9bb5c16f-67ac-4174-8427-1dfec65ee59e
function negativeS(S)
	S2=copy(S)	
	S2[S.>0].=0
	return S2
end;

# ╔═╡ bbb9060a-5004-423c-a01e-bbec85fb49b8
function positiveS(S)
S2=copy(S)
	S2[S.<0].=0
	return S2
end;

