# ╔═╡ c96b390d-f018-4ff7-8260-f8f8e5fe4f16
html"""<style>
pluto-output>div:has(img) {
    display: flex;    justify-content: center;
}
</style>
"""

# ╔═╡ 2768214f-3622-434a-af90-fceb86134608
fsignal = 10;

# ╔═╡ 446a2c7f-e0ba-4e41-a1c2-f05036a63b79
t1 =10/fsignal;

# ╔═╡ 20f9d03b-b711-4ca8-8699-fd2deb708d4d
begin
t0=0
	dt = 1e-3;
	t = t0:dt:t1;
end;

# ╔═╡ d1bb0cef-fff1-4b6b-af7c-5bc2c55dc9ad
begin
	y = (t) -> functions[s](2pi*fsignal*t);
	frange=0:.1:100;
	findex = findfirst(frange.==f);
	wrapped_signal = y.(t).*exp.(2pi*1im*f.*t);
	plot_signal = plot(t,y,label="",width=2)
	xlabel!("Zeit / s")
	title!("Zeitsignal")
		plot_circle=plot(real(wrapped_signal),imag(wrapped_signal),label="Herumgewickeltes Signal",width=2)
  	scatter!([sum(real(wrapped_signal))/length(wrapped_signal)],[sum(imag(wrapped_signal))/length(wrapped_signal)],label="Schwerpunkt")
  	xlims!(-1,1)
  	ylims!(-1,1)
	annotate!(0,0.9,"f = $f")
	xlabel!("Realteil")
	ylabel!("Imaginärteil") 
		E = [exp(2pi*1im*f*ti) for ti=t,f=frange]
	fft_vec_real = real.( transpose(y.(t)) * E )[1,:]./length(t)
	fft_vec_imag = imag.( transpose(y.(t)) * E )[1,:]./length(t)
	fft_vec_abs = abs.( transpose(y.(t)) * E )[1,:]./length(t)

	plot_fft=plot(frange,fft_vec_real,label="Realteil der Fouriertransformation",size=(1300,900),width=2,xlims=(0,50))
	plot!(frange,fft_vec_imag,label="Imaginärteil der Fouriertransformation",width=2,)
	scatter!([f],[fft_vec_real[findex]], label="Aktuell ausgewählte Frequenz")
	scatter!([f],[fft_vec_imag[findex]], label="Aktuell ausgewählte Frequenz")
	plot_abs=plot(frange, fft_vec_abs, title="Absolutbetrag der Fouriertransformation")
	plot_phase=plot(frange, atan.(fft_vec_real,fft_vec_imag),title="Phase der Fouriertransformation")
	xlabel!("Frequenz / Hz")
	nothing
end;

# ╔═╡ 656ce595-373e-4680-9f0d-8e9bb0321a08
plot(plot_signal,plot_circle,plot_fft,layout=@layout [a ; b c])

# ╔═╡ ead4b76d-2153-4183-a329-a0b623af3a0e
plot(plot_abs,plot_phase,size=(1000,400),width=2,xlims=(0,50))

